contracts/MyToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {ERC20Burnable} from "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

contract MyToken is ERC20, ERC20Burnable, AccessControl {

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor() ERC20("MyToken", "MTK") {
        _mint(msg.sender, 10 * 10 ** decimals());
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function burnFrom(uint256 amount) public {
        burnFrom(msg.sender, amount);
    }

    function decimals() public pure override returns (uint8) {
        return 8;
    }
}

contracts/TokenSale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {MyToken} from "./MyToken.sol";
import {MyNFT} from "./MyNFT.sol";

contract TokenSale {

    uint256 public ratio;
    uint256 public price;
    MyToken public paymentToken; // location of token contract
    MyNFT public nftCollection; // location of nft contract

    constructor(uint256 _ratio, uint256 _price, MyToken _paymentToken, MyNFT _nftCollection) {
        ratio = _ratio;
        price = _price;
        paymentToken = _paymentToken; // better to pass after we deploy, saves on gas
        nftCollection = _nftCollection;
    }

    function buyTokens() external payable {

        paymentToken.mint(msg.sender, msg.value * ratio);
        // need to mint tokens
    }

    function returnTokens(uint256 amount) external {

        paymentToken.burnFrom(msg.sender, amount); // don't need to check to see if they have the burn amount because if they don't it will revert
        payable(msg.sender).transfer(amount / ratio); // transfers eth back to msg.sender
    }

}

